catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var gr = new GlideRecord('x_g_ni4_interdivis_interdivision_work_order');
    gr.addQuery('sys_id', newValue);
    gr.query(doThis);

    function doThis(caller) {
        while (gr.next()) {
            g_form.setValue('to',caller.to);
			 g_form.setValue('Bureau_c',caller.Bureau_d);
			 g_form.setValue('Bureau_d',caller.Bureau_c);
			g_form.setValue('fcfy_c',caller.fcfy_d);
			 g_form.setValue('fcfy_d',caller.fcfy_c);
			g_form.setValue('funding_division',caller.service_provider_division);
			g_form.setValue('service_provider_division',caller.funding_division);
			 g_form.setValue('specification',caller.specification);
			g_form.setValue('object_class_c',caller.object_class_d);
			 g_form.setValue('object_class_d',caller.object_class_c);
			g_form.setValue('organization_c',caller.organization_d);
			 g_form.setValue('organization_d',caller.organization_c);
			g_form.setValue('phone_ext_c',caller.phone_ext_d);
			 g_form.setValue('phone_ext_d',caller.phone_ext_c);
			g_form.setValue('project_task_c',caller.project_task_d);
			 g_form.setValue('project_task_d',caller.project_task_c);
			g_form.setValue('requisition_number_d',caller.requisition_number_c);
			 g_form.setValue('requisition_number_c',caller.requisition_number_d);
			g_form.setValue('date_needed',caller.date_needed);
			g_form.setValue('or_estimate_requested',caller.or_estimate_requested);
			g_form.setValue('authorized_amount',caller.authorized_amount);
			g_form.setValue('work_order_number',caller.work_order_number);
			g_form.setValue('point_of_contact_d',caller.point_of_contact_c);
			g_form.setValue('point_of_contact_c',caller.point_of_contact_d);

        }

    }
}
