sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   if (newValue == '') {
		g_form.hideFieldMsg('object_class_c');
		return;

	}
	//check if it has 62. or 52
	var str1 = newValue.indexOf(62);
	var str2=newValue.indexOf(52);
	var rx = new RegExp(/^\d{4}$/);

	if (!rx.test(newValue)) {
		g_form.hideFieldMsg('object_class_c');
		g_form.setValue('object_class_c', '');
		g_form.showFieldMsg('object_class_c', 'Please enter 4 digits number only.', 'error');

	} else {
		if (str1 ==0 || str2==0){
			 g_form.hideFieldMsg('object_class_c');
		}
		else
			{
				g_form.setValue('object_class_c', '');
				g_form.showFieldMsg('object_class_c', 'Please enter 4 digits number start with 62 or 52.', 'error');
			}



	}

}
