sys_script_client.script:
function onLoad() {

    var sections = g_form.getSections();
    if (g_form.getValue('multiple_ptc') == 'true') {

        for (var i = 0; i < sections.length; i++) {

            // Get elements with class 'embedded'
            var embedded_elements = sections[i].getElementsByClassName("embedded");
            // Loop through those elements and when we get a valid match of the table name of the embedded list we hide it
            for (var i_element = 0; i_element < embedded_elements.length; i_element++) {
                if (embedded_elements[i_element].getAttribute("tab_list_name_raw")) {
                    embedded_elements[i_element].show();
                    return;
                }

            }
        }
    } 
	else {

		for (var j = 0; j < sections.length; j++) {

            // Get elements with class 'embedded'
            var embedded_elements = sections[j].getElementsByClassName("embedded");
            // Loop through those elements and when we get a valid match of the table name of the embedded list we hide it
            for (var i_element = 0; i_element < embedded_elements.length; i_element++) {
                if (embedded_elements[i_element].getAttribute("tab_list_name_raw")) {
                    embedded_elements[i_element].hide();
                    return;
                }
            }

        }
    }
}
