sys_script_include.script:
var Interdivision_help = Class.create();
Interdivision_help.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
//gs.log("jingping");
	getUser: function(){
		try {
			//gs.error("JINGPING" + this.getParameter('sysparm_name_userid'));
			var gr = new GlideRecord('sys_user');
			gr.addQuery('sys_id', this.getParameter('sysparm_name_userid'));
			gr.addQuery('u_class', 1);
			gr.query();

			if(gr.next()){
				gs.error("JINGPING" + this.getParameter('sysparm_name_userid'));
				var result = {};
				if (gr.name){
					result.name = gr.getValue('name');
				}

				if (gr.u_organizational_unit){
					result.ou = gr.getValue('u_organizational_unit');
				} else {
					result.ou = '';
				}
				if (gr.phone){
					result.phone = gr.getValue('phone');

				} else {
					result.phone = '';
				}
                if (gr.email){
					result.email = gr.getValue('email');
				} 
				if (gr.u_division){
					result.division = gr.getValue('u_division');
				} else {
					result.division = '';
				}

				return JSON.stringify(result);
			}			
		} catch(ex) {
		  gs.error("Failed at Update Remote: " + JSON.stringify(ex));
		}
	},


    type: 'Interdivision_help'
});
