sys_security_acl.script:
var myApproval = new GlideRecord('sysapproval_approver');
myApproval.addQuery('document_id', current.sys_id);
myApproval.addQuery('state', 'requested');
myApproval.addQuery('approver', gs.getUserID());
myApproval.query();
if (myApproval.next()) {
    answer = true;
} else {
    answer = false;
}
